mapper.update:
UPDATE NOTIFICATION set NOTIFICATION_TYPE = #{type} ,NOTIFICATION_MESSAGE = #{message, jdbcType=VARCHAR} 
,PARAMETER1 = #{parameter1, jdbcType=VARCHAR} ,PARAMETER2 = #{parameter2, jdbcType=VARCHAR} 
,PARAMETER3 = #{parameter3, jdbcType=VARCHAR}
where NOTIFICATION_ID = #{notificationId}

mapper.select:
SELECT notification_id as notificationId, member_id as memberId, notification_type as type, notification_message as message, reg_date as regDate, is_read as isRead, parameter1, parameter2, parameter3
FROM NOTIFICATION
where IS_READ = 0 and MEMBER_ID = #{memberId}

SELECT count(*)
FROM NOTIFICATION
where IS_READ = 0 and MEMBER_ID = #{memberId}
