mapper.update:
UPDATE SELLER SET 
	seller_name=#{sellerName},tel=#{tel},
	email=#{email},password=#{password},
	presentation=#{presentation, jdbcType=VARCHAR}
WHERE seller_id=#{sellerId}

mapper.select:
SELECT status 
FROM seller_status
WHERE seller_id= #{sellerId}

select count(*)
from SELLER
where business_number = #{businessNumber}

mapper.update:
UPDATE SELLER SET 
	accumulated_amount = #{accumulatedAmount}
WHERE seller_id=#{sellerId}
