	seller_name=#{sellerName, jdbcType=VARCHAR},tel=#{tel, jdbcType=VARCHAR},
	email=#{email, jdbcType=VARCHAR}, representative_name=#{representativeName, jdbcType=VARCHAR},
	presentation=#{presentation, jdbcType=VARCHAR}
WHERE seller_id=#{sellerId}
